            public void Hide(bool canTransition = true, Action<Callback> callback = null)
            {
                Callback callbackResults = new Callback();

                switch (transitionType)
                {
                    case TransitionType.Default:

                        OnHideScreenWidget();

                        callbackResults.result = "Widget Hidden.";
                        callbackResults.resultCode = Helpers.SuccessCode;

                        break;

                    case TransitionType.Translate:

                        if (widgetRect)
                        {
                            if (canTransition)
                            {
                                onWidgetTransition = true;
                                showWidget = false;

                                if (onWidgetTransition == false)
                                {
                                    callbackResults.result = "Widget Hidden.";
                                    callbackResults.resultCode = Helpers.SuccessCode;
                                }
                                else
                                {
                                    callbackResults.result = "Widget Not Hidden.";
                                    callbackResults.resultCode = Helpers.WarningCode;
                                }
                            }
                            else
                            {
                                widgetRect.anchoredPosition = widgetContainer.hiddenScreenPoint.anchoredPosition;
                            }
                        }

                        break;
                }

                callback?.Invoke(callbackResults);
            }

            public async Task<Callback> HideAsync(bool canTransition = true)
            {
                Callback callbackResults = new Callback();

                switch (transitionType)
                {
                    case TransitionType.Default:

                        OnHideScreenWidget();

                        callbackResults.result = "Widget Hidden.";
                        callbackResults.resultCode = Helpers.SuccessCode;

                        break;

                    case TransitionType.Translate:

                        if (widgetRect)
                        {
                            if (canTransition)
                            {
                                onWidgetTransition = true;
                                showWidget = false;

                                if (onWidgetTransition == false)
                                {
                                    callbackResults.result = "Widget Hidden.";
                                    callbackResults.resultCode = Helpers.SuccessCode;
                                }
                                else
                                {
                                    callbackResults.result = "Widget Not Hidden.";
                                    callbackResults.resultCode = Helpers.WarningCode;
                                }

                                while (onWidgetTransition)
                                    await Task.Yield();
                            }
                            else
                            {
                                widgetRect.anchoredPosition = widgetContainer.hiddenScreenPoint.anchoredPosition;
                            }
                        }

                        break;
                }

                return callbackResults;
            }

            void OnWidgetTransition()
            {
                if (onWidgetTransition)
                {
                    if (showWidget)
                    {
                        if (widgetRect)
                        {
                            if (!isTransitionState)
                                isTransitionState = true;

                            if (widgetContainer.hiddenScreenPoint != null && widgetContainer.visibleScreenPoint != null)
                            {
                                if (DatabaseManager.Instance.GetDefaultExecutionValue(RuntimeExecution.ScreenWidgetTransitionalSpeed).value > 0)
                                {
                                    Vector2 screenPoint = widgetRect.anchoredPosition;

                                    screenPoint = Vector2.Lerp(screenPoint, widgetContainer.visibleScreenPoint.anchoredPosition, DatabaseManager.Instance.GetDefaultExecutionValue(RuntimeExecution.ScreenWidgetTransitionalSpeed).value * Time.smoothDeltaTime);
                                    widgetRect.anchoredPosition = screenPoint;

                                    float distance = (widgetRect.anchoredPosition - widgetContainer.visibleScreenPoint.anchoredPosition).sqrMagnitude;

                                    if (distance <= 0.1f)
                                    {
                                        onWidgetTransition = false;
                                        isTransitionState = false;

                                        OnSetWidgetShowingState(true);
                                    }
                                }
                                else
                                    LogWarning($"Scene Assets Manager Instance Get Default Execution Times Is Not Set - Currently {DatabaseManager.Instance.GetDefaultExecutionValue(RuntimeExecution.ScreenWidgetTransitionalSpeed).value}.", this, () => OnWidgetTransition());

                            }
                            else
                                LogWarning($"Widget Container Hidden Screen Point | Widget Container visible Screen Point Is Null / Not Assigned In The Editor For Game Object : {gameObject.name}.", this, () => OnWidgetTransition());
                        }
                        else
                            LogWarning("Widget Rect Is Null.", this, () => OnWidgetTransition());

                    }
                    else
                    {
                        if (widgetRect)
                        {
                            if (!isTransitionState)
                                isTransitionState = true;

                            if (widgetContainer.hiddenScreenPoint != null && widgetContainer.visibleScreenPoint != null)
                            {
                                Vector2 screenPoint = widgetRect.anchoredPosition;
                                screenPoint = Vector2.Lerp(screenPoint, widgetContainer.hiddenScreenPoint.anchoredPosition, DatabaseManager.Instance.GetDefaultExecutionValue(RuntimeExecution.ScreenWidgetTransitionalSpeed).value * Time.smoothDeltaTime);

                                widgetRect.anchoredPosition = screenPoint;

                                float distance = (widgetRect.anchoredPosition - widgetContainer.hiddenScreenPoint.anchoredPosition).sqrMagnitude;

                                if (distance <= 0.1f)
                                {
                                    onWidgetTransition = false;
                                    isTransitionState = false;

                                    OnSetWidgetShowingState(false);
                                }
                            }
                            else
                                LogWarning("Widget Container Hidden Screen Point | Widget Container Visible Screen Point Is Null / Not Assigned In The Editor.", this, () => OnWidgetTransition());
                        }
                        else
                            LogWarning("Widget Rect Is Null.", this, () => OnWidgetTransition());
                    }
                }
                else
                    return;
            }

