            public static string GetColorGradientHexadecimal(Int32 numberOfSteps, Int32 step)
            {
                var r = 0.0;
                var g = 0.0;
                var b = 0.0;

                var h = (Double)step / numberOfSteps;
                var i = (Int32)(h * 6);
                var f = h * 6.0f - i;
                var q = 1 - f;

                switch (i % 6)
                {
                    case 0:

                        r = 1;
                        g = f;
                        b = 0;

                        break;

                    case 1:

                        r = q;
                        g = 1;
                        b = 0;

                        break;

                    case 2:

                        r = 0;
                        g = 1;
                        b = f;

                        break;

                    case 3:

                        r = 0;
                        g = q;
                        b = 1;

                        break;

                    case 4:

                        r = f;
                        g = 0;
                        b = 1;

                        break;

                    case 5:

                        r = 1;
                        g = 0;
                        b = q;

                        break;
                }

                return "#" + ((Int32)(r * 255)).ToString("X2") + ((Int32)(g * 255)).ToString("X2") + ((Int32)(b * 255)).ToString("X2");
            }


